css:
atom-text-editor::shadow .git-blame-mount,
.atom-text-editor .git-blame-mount {
  position: relative;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-resize-handle,
.atom-text-editor .git-blame-mount .git-blame .git-blame-resize-handle {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: auto;
  width: 10px;
  cursor: col-resize;
  z-index: 1;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  overflow: hidden;
  background-color: #282c34;
  border-right: 1px solid #181a1f;
  border-left: 1px solid #181a1f;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines {
  padding-bottom: 30px;
  white-space: nowrap;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line {
  padding-left: 4px;
  overflow: hidden;
  text-overflow: ellipsis;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.line-bg-lighter,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.line-bg-lighter {
  background-color: #31363f;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.line-bg-darker,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.line-bg-darker {
  background-color: #1f2228;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line .hash,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line .hash,
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line .date,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line .date {
  font-size: 90%;
  padding-right: 10px;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line a.hash,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line a.hash {
  color: #9da5b4;
  text-decoration: none;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line a.hash:hover,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line a.hash:hover {
  text-decoration: underline;
}
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.no-commit .committer,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.no-commit .committer,
atom-text-editor::shadow .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.no-commit .date,
.atom-text-editor .git-blame-mount .git-blame .git-blame-scroller .blame-lines .blame-line.no-commit .date {
  display: none;
}
